const resetPeep = ({ stage, peep }) => {
	const direction = Math.random() > 0.5 ? 1 : -1
	const offsetY = 100 - 250 * gsap.parseEase('power2.in')(Math.random())
	const startY = stage.height - peep.height + offsetY
	let startX
	let endX
	if (direction === 1) {
		startX = -peep.width
		endX = stage.width
		peep.scaleX = 1
	} else {
		startX = stage.width + peep.width
		endX = 0
		peep.scaleX = -1
	}
	peep.x = startX
	peep.y = startY
	peep.anchorY = startY
	return {
		startX,
		startY,
		endX
	}
}
