function AI(playerToControl) {
	var ctl = playerToControl;
	var State = {
		WAITING: 0,
		FOLLOWING: 1,
		AIMING: 2
	}
	var currentState = State.FOLLOWING;
	function repeat(cb, cbFinal, interval, count) {
		var timeout = function () {
			repeat(cb, cbFinal, interval, count - 1);
		}
		if (count <= 0) {
			cbFinal();
		} else {
			cb();
			setTimeout(function () {
				repeat(cb, cbFinal, interval, count - 1);
			}, interval);
		}
	}
	function aimAndFire() {
		var numRepeats = Math.floor(5 + Math.random() * 5);
		function randomMove() {
			if (Math.random() > .5) {
				ctl.move(-distance);
			} else {
				ctl.move(distance);
			}
		}
		function randomAimAndFire() {
			var d = Math.floor(Math.random() * 3 - 1);
			opponent.setAim(d);
			opponent.fire();
			currentState = State.FOLLOWING;
		}
		repeat(randomMove, randomAimAndFire, 250, numRepeats);
	}
	function moveTowardsBall() {
		if (ball.getPosition()[1] >= ctl.getPosition()[1] + ctl.getSize() / 2) {
			ctl.move(distance);
		} else {
			ctl.move(-distance);
		}
		setTimeout(function () {
			currentState = State.FOLLOWING;
		}, 400);
	}
	function update() {
		switch (currentState) {
			case State.FOLLOWING:
				if (ball.getOwner() === ctl) {
					currentState = State.AIMING;
					aimAndFire();
				} else {
					moveTowardsBall();
					currentState = State.WAITING;
				}
			case State.WAITING:
				break;
			case State.AIMING:
				break;
		}
	}
	return {
		update: update
	}
}
