function cdf0(x) {
	function normal(x, mu, sigma) {
		return stdNormal((x - mu) / sigma);
	}
	function stdNormal(z) {
		var j, k, kMax, m, values, total, subtotal, item, z2, z4, a, b;
		if (z < -6) { return 0; }
		if (z > 6) { return 1; }
		m = 1;
		b = z;
		z2 = z * z;
		z4 = z2 * z2;
		values = [];
		for (k = 0; k < 100; k += 2) {
			a = 2 * k + 1;
			item = b / (a * m);
			item *= (1 - (a * z2) / ((a + 1) * (a + 2)));
			values.push(item);
			m *= (4 * (k + 1) * (k + 2));
			b *= z4;
		}
		total = 0;
		for (k = 49; k >= 0; k--) {
			total += values[k];
		}
		return 0.5 + 0.3989422804014327 * total;
	}
	return normal(x, 100, 15);
}
