function h2rgb(h) {
	let r = Math.abs(h * 6 - 3) - 1;
	let g = 2 - Math.abs(h * 6 - 2);
	let b = 2 - Math.abs(h * 6 - 4);
	return saturate(float3(r, g, b));
}
