	let lines1 = lines.filter(x => !isEmptyOrWhiteSpace(x));
	let lines2 = [];
	for (const l of lines1) {
		let lNew = '';
		for (let i = 0; i < l.length; i++) {
			if (l[i] == ' ') continue;
			for (let x = 0; x < f; x++) lNew += l[i] + ' ';
		}
		lines2.push(lNew.trim());
	}
	let lines3 = [];
	for (const l of lines2) { for (let i = 0; i < f; i++) { lines3.push(l); } }
	return lines3;
