function test7_function() {
	C = new CCanvas(dTable, {}, {}, gameloop_start, gameloop_stop, 'cc');
	let ctx = C.cx;
	let canvas = C.cv;
	let h = canvas.height;
	let w = canvas.width;
	let cw = w / 2;
	let ch = h / 2;
	let subStepCount = 10;
	let scale = 10;
	function plot(func, col, lineWidth) {
		let invScale = 1 / scale;
		let top = ch * invScale;
		let bottom = -ch * invScale;
		let subStep = invScale / subStepCount;
		let x, y, yy, xx, subX;
		let start = (-cw - 1) * invScale;
		let end = (cw + 1) * invScale;
		ctx.strokeStyle = col;
		ctx.lineWidth = lineWidth * invScale;
		ctx.beginPath();
		for (x = start; x < end; x += invScale) {
			for (subX = 0; subX < invScale; subX += subStep) {
				y = func(x + subX);
				if (yy !== undefined) {
					if (y > top || y < bottom) {
						if (yy < top && yy > bottom) {
							ctx.lineTo(x + subX, y);
						}
					} else {
						if (yy > top || yy < bottom) {
							ctx.moveTo(xx, yy);
						}
						if (subX === 0) {
							if (y > bottom && y < top) {
								if (Math.abs(yy - y) > (top - bottom) * (1 / 3)) {
									ctx.moveTo(x, y);
								} else {
									ctx.lineTo(x, y);
								}
							}
						}
					}
				} else {
					if (subX === 0) {
						ctx.moveTo(x, y);
					}
				}
				yy = y;
				xx = x + subX;
			}
		}
		ctx.stroke();
	}
	ctx.setTransform(scale, 0, 0, -scale, cw, ch);
	plot(x => 0, 'white', 2);
	plot(x => 5 * Math.sin(x), 'white', 2);
	plot(x => 5 * Math.sin(x), 'white', 2);
}
