function tsRec(x) {
	if (isList(x)) { return '[' + x.map(tsRec).join('') + ']'; }
	if (isDict(x)) {
		if ('_set' in x) {
			return '{' + tsRec(x._set) + '}';
		} else if ('_tuple' in x) {
			return '(' + tsRec(x._tuple) + ')'
		} else if ('type' in x) {
			return tsAction(x)
		} else { return 'obj unknown'; }
	} else return 'type unknown';
}
